<?php/** * Validatorius, kuris tikrina ar paduodamas input'as nera tustcias; * @param $field_input * @param $field * @return bool */function validate_not_empty($field_input, &$field){    if ($field_input === '') {        $field['error'] = 'Laukas negali būti tuščias';        return false;    } else {        return true;    }}/** * Validuoja ar paduodamas input'as yra skaicius * @param $field_input * @param $field * @return bool */function validate_is_number($field_input, &$field){    if (!is_numeric($field_input)) {        $field['error'] = 'Turite įrašyti skaičių';        return false;    } else {        return true;    }}/** * Validuoja ar passwordas yra ilgesnis nei 8 simboliai * @param $field_input * @param $field * @return bool */function validate_password($field_input, &$field){    $length = strlen($field_input);    if ($length < 8) {        $field['error'] = 'Slaptažodis turi būti iš daugiau nei 8';        return false;    } else {        return true;    }}function validate_email($field_input, &$field){    $string = str_split($field_input);    if (in_array('@', $string)) {        return true;    } else {        $field['error'] = 'Blogai įvestas emailas';        return false;    }}function validate_fields_match($filtered_input, &$form, $params){    foreach ($params as $param) {        $array[] = $filtered_input[$param];    }    if (count(array_unique($array)) === 1) {        return true;    } else {$form['error']='Nesutampa slaptažodžiai';        return false;    }}